void UIMainWindows::updatePai()
{
    //更新下一摸牌的位置
    if(mopaiDirection == 0){//南
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
            mopaiPos++;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
        }

        if(mopaiPos >= zhangshuSouth/2){
            mopaiDirection = 3;//西
            mopaiPos = 0;
            mopaiCeng = 0;
        }
    }else if(mopaiDirection == 1){//东
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
            mopaiPos++;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
        }

        if(mopaiPos >= zhangshuEast/2){
            mopaiDirection = 0;//南
            mopaiPos = 0;
            mopaiCeng = 0;
        }
    }else if(mopaiDirection == 2){//北
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
            mopaiPos++;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
        }

        if(mopaiPos >= zhangshuNorth/2){
            mopaiDirection = 1;//东
            mopaiPos = 0;
            mopaiCeng = 0;
        }
    }else if(mopaiDirection == 3){//西
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
            mopaiPos++;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
        }

        if(mopaiPos >= zhangshuWest/2){
            mopaiDirection = 2;//北
            mopaiPos = 0;
            mopaiCeng = 0;
        }
    }

    //预测
    yuceDirection  = mopaiDirection;
    yucePos = mopaiPos;
    yuceCeng = mopaiCeng;
}

void UIMainWindows::undoPai()
{
    //更新下一摸牌的位置
    if(mopaiDirection == 0){//南
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
            mopaiPos--;
        }

        if(mopaiPos < 0){
            mopaiDirection = 1;//东
            mopaiPos = zhangshuEast/2;
            mopaiCeng = 1;
        }
    }else if(mopaiDirection == 1){//东
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
             mopaiPos--;
        }

        if(mopaiPos < 0 ){
            mopaiDirection = 2;//北
            mopaiPos = zhangshuEast/2;
            mopaiCeng = 1;
        }
    }else if(mopaiDirection == 2){//北
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
            mopaiPos--;
        }

        if(mopaiPos < 0){
            mopaiDirection = 3;//西
            mopaiPos = zhangshuNorth/2;
            mopaiCeng = 1;
        }
    }else if(mopaiDirection == 3){//西
        if(mopaiCeng == 1){//第二层
            mopaiCeng = 0;
        }else if(mopaiCeng == 0){//第一层
            mopaiCeng = 1;
            mopaiPos--;
        }

        if(mopaiPos < 0){
            mopaiDirection = 0;//南
            mopaiPos = zhangshuWest/2;
            mopaiCeng = 1;
        }
    }

    //预测
    yuceDirection  = mopaiDirection;
    yucePos = mopaiPos;
    yuceCeng = mopaiCeng;
}

