void UIMainWindows::displayError(QAbstractSocket::SocketError)
{
    qDebug() << tcpClient->errorString();
    //关闭套接字的I / O设备，并调用DisconnectFromHost（）关闭套接字的连接。
    //如果有等待写入的等待数据，QAbstractSocket将进入ClosingState并等待直到所有数据都被写入。
    //最终，它将进入UnconnectedState并发出断开连接（）信号。
    tcpClient->close();

    ui->clientStatusLabel->setText(tr("客户端就绪"));
    // ui->sendButton->setEnabled(true);
}
