//随机打乱list顺序
template <class T>
void shuffle(list<T>& List) {
    vector<T> tempList;
    for(auto i:List){
        tempList.push_back(i);
    }

    std::random_shuffle(tempList.begin(), tempList.end());

    List.clear();
    for(auto i: tempList){
        List.push_back(i);
    }
}


