bool UIMainWindows::isSequence(const std::list<Mj> & handMjs, int pai) const
{
    int nPai1,nPai2;
    nPai1 = pai + 1;
    nPai2 = pai + 2;

    bool b1 = false,b2 = false;
    for(auto i: handMjs){
       if(i.getResult() == nPai1){
            b1 = true;
            break;
        }
     }
     for(auto i: handMjs){
        if(i.getResult() == nPai2){
            b2 = true;
            break;
        }
       }

       //牌1牌2都存在就为真,其余都为假
     return (b1 && b2);
}

bool UIMainWindows::isTriplet(const std::list<Mj> & handMjs, int pai) const
{
    int cnt = 0;
    for(auto i:handMjs){
        if(i.getResult() == pai){
            cnt++;
        }
    }
    return cnt >= 3;
}
